.dbconfig defensive off
BEGIN;
PRAGMA writable_schema = on;
PRAGMA foreign_keys = off;
PRAGMA encoding = 'UTF-8';
PRAGMA page_size = '4096';
PRAGMA auto_vacuum = '0';
PRAGMA user_version = '0';
PRAGMA application_id = '0';
CREATE TABLE sqlite_sequence(name,seq);
CREATE TABLE "django_migrations" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app" varchar(255) NOT NULL, "name" varchar(255) NOT NULL, "applied" datetime NOT NULL);
CREATE TABLE "auth_group_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "auth_user_groups" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "group_id" integer NOT NULL REFERENCES "auth_group" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "auth_user_user_permissions" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "permission_id" integer NOT NULL REFERENCES "auth_permission" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "django_admin_log" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "object_id" text NULL, "object_repr" varchar(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" text NOT NULL, "content_type_id" integer NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "action_time" datetime NOT NULL);
CREATE TABLE "crm_opportunity" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "opportunity_id" varchar(50) NOT NULL UNIQUE, "stage" varchar(50) NOT NULL, "product_category" varchar(50) NOT NULL, "moq_units" integer NULL, "order_value" decimal NULL, "created_date" date NOT NULL, "next_followup" date NULL, "notes" text NOT NULL, "is_open" bool NOT NULL, "lead_id" bigint NOT NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "product_type" varchar(50) NOT NULL, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "aihub_aiagent" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "code" varchar(50) NOT NULL UNIQUE, "role" varchar(100) NOT NULL, "category" varchar(100) NOT NULL, "description" text NOT NULL, "system_prompt" text NOT NULL);
CREATE TABLE "aihub_aimessage" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "sender" varchar(10) NOT NULL, "content" text NOT NULL, "created_at" datetime NOT NULL, "conversation_id" bigint NOT NULL REFERENCES "aihub_aiconversation" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "aihub_aiconversation" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NOT NULL, "agent_id" bigint NOT NULL REFERENCES "aihub_aiagent" ("id") DEFERRABLE INITIALLY DEFERRED, "lead_id" bigint NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED, "user_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "django_content_type" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "app_label" varchar(100) NOT NULL, "model" varchar(100) NOT NULL);
CREATE TABLE "auth_permission" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content_type_id" integer NOT NULL REFERENCES "django_content_type" ("id") DEFERRABLE INITIALLY DEFERRED, "codename" varchar(100) NOT NULL, "name" varchar(255) NOT NULL);
CREATE TABLE "auth_group" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE);
CREATE TABLE "auth_user" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "password" varchar(128) NOT NULL, "last_login" datetime NULL, "is_superuser" bool NOT NULL, "username" varchar(150) NOT NULL UNIQUE, "last_name" varchar(150) NOT NULL, "email" varchar(254) NOT NULL, "is_staff" bool NOT NULL, "is_active" bool NOT NULL, "date_joined" datetime NOT NULL, "first_name" varchar(150) NOT NULL);
CREATE TABLE "crm_customer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "customer_code" varchar(50) NOT NULL UNIQUE, "account_brand" varchar(200) NOT NULL, "contact_name" varchar(200) NOT NULL, "email" varchar(254) NOT NULL, "phone" varchar(50) NOT NULL, "market" varchar(10) NOT NULL, "is_active" bool NOT NULL, "created_date" date NULL, "notes" text NOT NULL, "lead_id" bigint NOT NULL UNIQUE REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "shipping_address1" varchar(255) NOT NULL, "shipping_address2" varchar(255) NOT NULL, "shipping_city" varchar(100) NOT NULL, "shipping_country" varchar(100) NOT NULL, "shipping_name" varchar(200) NOT NULL, "shipping_postcode" varchar(20) NOT NULL, "shipping_state" varchar(100) NOT NULL);
CREATE TABLE "crm_lead" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "market" varchar(10) NOT NULL, "lead_id" varchar(50) NOT NULL UNIQUE, "account_brand" varchar(200) NOT NULL, "contact_name" varchar(200) NOT NULL, "email" varchar(254) NOT NULL, "phone" varchar(50) NOT NULL, "source" varchar(50) NOT NULL, "lead_type" varchar(50) NOT NULL, "lead_status" varchar(50) NOT NULL, "priority" varchar(20) NOT NULL, "owner" varchar(100) NOT NULL, "created_date" date NOT NULL, "next_followup" date NULL, "notes" text NOT NULL, "attachment" varchar(100) NULL, "budget" varchar(100) NOT NULL, "city" varchar(100) NOT NULL, "country" varchar(100) NOT NULL, "order_quantity" varchar(100) NOT NULL, "preferred_contact_time" varchar(100) NOT NULL, "product_interest" varchar(200) NOT NULL, "company_website" varchar(255) NOT NULL);
CREATE TABLE "crm_leadactivity" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NOT NULL, "activity_type" varchar(40) NOT NULL, "description" text NOT NULL, "lead_id" bigint NOT NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_leadcomment" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NOT NULL, "author" varchar(100) NOT NULL, "content" text NOT NULL, "pinned" bool NOT NULL, "is_ai" bool NOT NULL, "lead_id" bigint NOT NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_leadtask" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL, "description" text NOT NULL, "due_date" date NULL, "status" varchar(20) NOT NULL, "priority" varchar(20) NOT NULL, "assigned_to" varchar(100) NOT NULL, "created_at" datetime NOT NULL, "completed_at" datetime NULL, "lead_id" bigint NOT NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_opportunitytask" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL, "description" text NOT NULL, "due_date" date NULL, "status" varchar(20) NOT NULL, "priority" varchar(20) NOT NULL, "assigned_to" varchar(100) NOT NULL, "created_at" datetime NOT NULL, "completed_at" datetime NULL, "opportunity_id" bigint NOT NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_product" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "product_code" varchar(50) NOT NULL UNIQUE, "name" varchar(200) NOT NULL, "product_type" varchar(50) NOT NULL, "product_category" varchar(50) NOT NULL, "default_gsm" varchar(50) NOT NULL, "default_fabric" varchar(100) NOT NULL, "default_moq" integer NULL, "default_price" decimal NULL, "notes" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "image" varchar(100) NULL);
CREATE TABLE "crm_accessory" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "accessory_code" varchar(50) NOT NULL UNIQUE, "name" varchar(200) NOT NULL, "accessory_type" varchar(100) NOT NULL, "size" varchar(100) NOT NULL, "color" varchar(100) NOT NULL, "material" varchar(100) NOT NULL, "finish" varchar(100) NOT NULL, "supplier" varchar(200) NOT NULL, "price_per_unit" decimal NULL, "notes" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "image" varchar(100) NULL);
CREATE TABLE "crm_fabric" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "fabric_code" varchar(50) NOT NULL UNIQUE, "name" varchar(200) NOT NULL, "fabric_group" varchar(100) NOT NULL, "fabric_type" varchar(100) NOT NULL, "weave" varchar(100) NOT NULL, "knit_structure" varchar(100) NOT NULL, "construction" varchar(200) NOT NULL, "composition" varchar(200) NOT NULL, "gsm" varchar(50) NOT NULL, "stretch_type" varchar(100) NOT NULL, "surface" varchar(100) NOT NULL, "handfeel" varchar(100) NOT NULL, "drape" varchar(100) NOT NULL, "warmth" varchar(100) NOT NULL, "weight_class" varchar(100) NOT NULL, "breathability" varchar(100) NOT NULL, "sheerness" varchar(100) NOT NULL, "shrinkage" varchar(100) NOT NULL, "durability" varchar(100) NOT NULL, "color_options" varchar(255) NOT NULL, "price_per_kg" decimal NULL, "price_per_meter" decimal NULL, "notes" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "image" varchar(100) NULL);
CREATE TABLE "crm_trim" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "trim_code" varchar(50) NOT NULL UNIQUE, "name" varchar(200) NOT NULL, "trim_type" varchar(100) NOT NULL, "width" varchar(50) NOT NULL, "color" varchar(100) NOT NULL, "material" varchar(100) NOT NULL, "price_per_meter" decimal NULL, "notes" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "image" varchar(100) NULL);
CREATE TABLE "crm_fabrictypemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE "crm_handfeelmaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE "crm_knitstructuremaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE "crm_surfacemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE "crm_weavemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE);
CREATE TABLE "crm_fabricchoicemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "category" varchar(50) NOT NULL);
CREATE TABLE "crm_fabricnamemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(150) NOT NULL UNIQUE, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_gsmrangemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(50) NOT NULL UNIQUE, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_productcategorymaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_producttypemaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_fabricgroupmaster" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL UNIQUE, "created_at" datetime NOT NULL, "is_active" bool NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_inventoryitem" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(200) NOT NULL, "category" varchar(50) NOT NULL, "sku" varchar(100) NOT NULL, "code" varchar(100) NOT NULL, "unit_type" varchar(50) NOT NULL, "unit_cost" decimal NULL, "quantity" decimal NOT NULL, "min_level" decimal NOT NULL, "location" varchar(200) NOT NULL, "image" varchar(100) NULL, "notes" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "accessory_id" bigint NULL REFERENCES "crm_accessory" ("id") DEFERRABLE INITIALLY DEFERRED, "fabric_id" bigint NULL REFERENCES "crm_fabric" ("id") DEFERRABLE INITIALLY DEFERRED, "product_id" bigint NULL REFERENCES "crm_product" ("id") DEFERRABLE INITIALLY DEFERRED, "thread_option_id" bigint NULL REFERENCES "crm_threadoption" ("id") DEFERRABLE INITIALLY DEFERRED, "trim_id" bigint NULL REFERENCES "crm_trim" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_event" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(255) NOT NULL, "start_datetime" datetime NOT NULL, "end_datetime" datetime NULL, "event_type" varchar(50) NOT NULL, "created_at" datetime NOT NULL, "priority" varchar(20) NOT NULL, "status" varchar(20) NOT NULL, "lead_id" bigint NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED, "note" text NOT NULL, "assigned_to_email" varchar(254) NULL, "assigned_to_name" varchar(120) NULL, "production_stage" varchar(40) NOT NULL, "reminder_minutes_before" integer unsigned NULL CHECK ("reminder_minutes_before" >= 0), "reminder_sent" bool NOT NULL, "updated_at" datetime NOT NULL, "ai_note" text NULL);
CREATE TABLE "crm_threadoption" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "thread_code" varchar(50) NOT NULL UNIQUE, "name" varchar(200) NOT NULL, "brand" varchar(100) NOT NULL, "color" varchar(100) NOT NULL, "count" varchar(100) NOT NULL, "created_at" datetime NOT NULL, "image" varchar(100) NULL, "is_active" bool NOT NULL, "notes" text NOT NULL, "price_per_cone" decimal NULL, "thread_type" varchar(100) NOT NULL, "updated_at" datetime NOT NULL, "use_for" varchar(200) NOT NULL);
CREATE TABLE "crm_inventoryreorder" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "created_at" datetime NULL, "inventory_item_id" bigint NULL REFERENCES "crm_inventoryitem" ("id") DEFERRABLE INITIALLY DEFERRED, "quantity" decimal NOT NULL);
CREATE TABLE "crm_leadaimessage" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "content" text NOT NULL, "created_at" datetime NOT NULL, "agent_id" bigint NULL REFERENCES "crm_aiagent" ("id") DEFERRABLE INITIALLY DEFERRED, "lead_id" bigint NOT NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "message_type" varchar(20) NOT NULL);
CREATE TABLE "crm_aiagent" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "description" text NOT NULL, "system_prompt" text NOT NULL, "is_active" bool NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "category" varchar(30) NOT NULL);
CREATE TABLE "crm_productionstage" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "actual_end" date NULL, "actual_start" date NULL, "ai_note" text NULL, "color_tag" varchar(20) NOT NULL, "created_at" datetime NOT NULL, "display_name" varchar(50) NOT NULL, "notes" text NOT NULL, "planned_end" date NULL, "planned_start" date NULL, "stage_key" varchar(20) NOT NULL, "status" varchar(20) NOT NULL, "updated_at" datetime NOT NULL, "order_id" bigint NOT NULL REFERENCES "crm_productionorder" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_opportunityfile" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "file" varchar(100) NOT NULL, "original_name" varchar(255) NOT NULL, "uploaded_at" datetime NOT NULL, "uploaded_by" varchar(100) NOT NULL, "opportunity_id" bigint NOT NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_productionorder" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "order_code" varchar(50) NOT NULL UNIQUE, "order_type" varchar(20) NOT NULL, "created_at" datetime NOT NULL, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED, "ai_note" text NULL, "bulk_deadline" date NULL, "qty_reject" integer unsigned NOT NULL CHECK ("qty_reject" >= 0), "qty_total" integer unsigned NOT NULL CHECK ("qty_total" >= 0), "sample_deadline" date NULL, "fabric_used_kg" decimal NULL, "factory_location" varchar(10) NOT NULL, "lead_id" bigint NULL REFERENCES "crm_lead" ("id") DEFERRABLE INITIALLY DEFERRED, "notes" text NOT NULL, "product_id" bigint NULL REFERENCES "crm_product" ("id") DEFERRABLE INITIALLY DEFERRED, "status" varchar(20) NOT NULL, "title" varchar(200) NOT NULL, "updated_at" datetime NOT NULL, "style_image" varchar(100) NULL, "actual_cost_per_piece_bdt" decimal NULL, "actual_total_cost_bdt" decimal NULL, "fabric_cost_per_kg_bdt" decimal NULL, "fabric_leftover_kg" decimal NULL, "fabric_received_kg" decimal NULL, "fabric_required_kg" decimal NULL, "fabric_total_cost_bdt" decimal NULL, "fabric_waste_kg" decimal NULL, "fabric_waste_percent" decimal NULL, "material_total_cost_bdt" decimal NULL, "production_total_cost_bdt" decimal NULL, "remake_cost_bdt" decimal NULL, "remake_qty" integer unsigned NULL CHECK ("remake_qty" >= 0), "remake_required" bool NOT NULL, "material_accessories_cost_bdt" decimal NULL, "material_label_cost_bdt" decimal NULL, "material_other_cost_bdt" decimal NULL, "material_thread_cost_bdt" decimal NULL, "material_zipper_cost_bdt" decimal NULL, "production_cutting_cost_bdt" decimal NULL, "production_finishing_cost_bdt" decimal NULL, "production_other_cost_bdt" decimal NULL, "production_overhead_cost_bdt" decimal NULL, "production_packing_cost_bdt" decimal NULL, "production_sewing_cost_bdt" decimal NULL, "accessories_note" text NOT NULL, "color_info" varchar(200) NOT NULL, "extra_order_note" text NOT NULL, "packaging_note" text NOT NULL, "size_ratio_note" text NOT NULL, "style_name" varchar(200) NOT NULL);
CREATE TABLE "crm_productionorderattachment" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "file" varchar(100) NOT NULL, "name" varchar(255) NOT NULL, "created_at" datetime NOT NULL, "order_id" bigint NOT NULL REFERENCES "crm_productionorder" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_invoice" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "invoice_number" varchar(50) NOT NULL UNIQUE, "issue_date" date NOT NULL, "due_date" date NULL, "currency" varchar(10) NOT NULL, "subtotal" decimal NOT NULL, "shipping_amount" decimal NOT NULL, "discount_amount" decimal NOT NULL, "tax_amount" decimal NOT NULL, "total_amount" decimal NOT NULL, "paid_amount" decimal NOT NULL, "status" varchar(20) NOT NULL, "notes" text NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "crm_productionorder" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_shipment" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "carrier" varchar(20) NOT NULL, "tracking_number" varchar(100) NOT NULL, "ship_date" date NULL, "shipment_type" varchar(20) NOT NULL, "box_count" integer unsigned NULL CHECK ("box_count" >= 0), "total_weight_kg" decimal NULL, "cost_bdt" decimal NULL, "status" varchar(20) NOT NULL, "last_tracking_status" varchar(200) NOT NULL, "last_tracking_check" datetime NULL, "delivered_at" datetime NULL, "notes" text NOT NULL, "created_at" datetime NOT NULL, "updated_at" datetime NOT NULL, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED, "order_id" bigint NULL REFERENCES "crm_productionorder" ("id") DEFERRABLE INITIALLY DEFERRED, "cost_cad" decimal NULL, "rate_bdt_per_cad" decimal NOT NULL);
CREATE TABLE "crm_bdstaff" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(120) NOT NULL, "role" varchar(120) NOT NULL, "is_active" bool NOT NULL, "base_salary_bdt" decimal NOT NULL, "join_date" date NULL, "note" text NOT NULL);
CREATE TABLE "crm_bdstaffmonth" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "year" integer NOT NULL, "month" integer NOT NULL, "base_salary_bdt" decimal NOT NULL, "overtime_hours" decimal NOT NULL, "overtime_rate_bdt" decimal NOT NULL, "overtime_total_bdt" decimal NOT NULL, "bonus_bdt" decimal NOT NULL, "deduction_bdt" decimal NOT NULL, "final_pay_bdt" decimal NOT NULL, "is_paid" bool NOT NULL, "paid_date" date NULL, "note" text NOT NULL, "staff_id" bigint NOT NULL REFERENCES "crm_bdstaff" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_exchangerate" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "cad_to_bdt" decimal NOT NULL, "updated_at" datetime NOT NULL);
CREATE TABLE "crm_moneytransfer" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "amount_cad" decimal NOT NULL, "amount_bdt" decimal NOT NULL, "receiver_name" varchar(120) NOT NULL, "note" text NOT NULL, "sent_method" varchar(10) NOT NULL, "created_at" datetime NOT NULL, "bd_entry_id" bigint NULL UNIQUE REFERENCES "crm_accountingentry" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingmonthclose" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "year" integer NOT NULL, "month" integer NOT NULL, "side" varchar(3) NOT NULL, "is_closed" bool NOT NULL, "note" varchar(255) NOT NULL, "closed_at" datetime NOT NULL, "closed_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingentryaudit" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "action" varchar(10) NOT NULL, "changed_at" datetime NOT NULL, "before_data" text NULL CHECK ((JSON_VALID("before_data") OR "before_data" IS NULL)), "after_data" text NULL CHECK ((JSON_VALID("after_data") OR "after_data" IS NULL)), "note" varchar(255) NOT NULL, "changed_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "entry_id" bigint NOT NULL REFERENCES "crm_accountingentry" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingmonthlock" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "year" integer NOT NULL, "month" integer NOT NULL, "is_closed" bool NOT NULL, "closed_at" datetime NULL, "closed_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingentry" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "date" date NOT NULL, "side" varchar(2) NOT NULL, "direction" varchar(3) NOT NULL, "main_type" varchar(30) NOT NULL, "sub_type" varchar(80) NOT NULL, "currency" varchar(3) NOT NULL, "amount_original" decimal NOT NULL, "rate_to_cad" decimal NOT NULL, "rate_to_bdt" decimal NOT NULL, "amount_cad" decimal NOT NULL, "amount_bdt" decimal NOT NULL, "description" text NOT NULL, "created_at" datetime NOT NULL, "created_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "customer_id" bigint NULL REFERENCES "crm_customer" ("id") DEFERRABLE INITIALLY DEFERRED, "opportunity_id" bigint NULL REFERENCES "crm_opportunity" ("id") DEFERRABLE INITIALLY DEFERRED, "production_order_id" bigint NULL REFERENCES "crm_productionorder" ("id") DEFERRABLE INITIALLY DEFERRED, "shipment_id" bigint NULL REFERENCES "crm_shipment" ("id") DEFERRABLE INITIALLY DEFERRED, "status" varchar(20) NOT NULL, "internal_note" text NOT NULL, "transfer_ref" varchar(50) NOT NULL, "linked_entry_id" bigint NULL REFERENCES "crm_accountingentry" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingattachment" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "uploaded_at" datetime NOT NULL, "entry_id" bigint NOT NULL REFERENCES "crm_accountingentry" ("id") DEFERRABLE INITIALLY DEFERRED, "original_name" varchar(255) NOT NULL, "uploaded_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "file" varchar(100) NOT NULL);
CREATE TABLE "crm_accountingdocument" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "side" varchar(2) NOT NULL, "doc_type" varchar(20) NOT NULL, "title" varchar(200) NOT NULL, "vendor" varchar(200) NOT NULL, "amount" decimal NULL, "doc_date" date NULL, "file" varchar(100) NOT NULL, "original_name" varchar(255) NOT NULL, "note" text NOT NULL, "uploaded_at" datetime NOT NULL, "linked_entry_id" bigint NULL REFERENCES "crm_accountingentry" ("id") DEFERRABLE INITIALLY DEFERRED, "uploaded_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE "crm_accountingmonthlytarget" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "side" varchar(2) NOT NULL, "year" integer unsigned NOT NULL CHECK ("year" >= 0), "month" integer unsigned NOT NULL CHECK ("month" >= 0), "target_bdt" decimal NOT NULL, "updated_at" datetime NOT NULL, "updated_by_id" integer NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED);
